import { Component } from '@angular/core';
import { UsersService } from '../services/users.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})
export class CreateUserComponent {
  userToUpdate: any = {};


  constructor(
    private usersService: UsersService,
    private router: Router
  ) {}

  ngOnInit(): void {
    // Puedes llamar aquí a la lógica para obtener o inicializar los datos del usuario a actualizar
  }

  createUser(): void {
    this.usersService.createUsers(this.userToUpdate).subscribe((updatedUser: any) => {
      console.log('Usuario actualizado:', updatedUser);
      // Puedes redirigir a otra página o mostrar un mensaje de éxito aquí
    });
  }
}
<div class="update-form">
    <h2>Actualizar Usuario</h2>
    <form (ngSubmit)="updateUser()">
      <div>
        <label for="idUser">ID de Usuario:</label>
        <input type="number" id="idUser" [(ngModel)]="userToUpdate.idUser" name="idUser" required>
      </div>
      <div>
        <label for="name">Nombre:</label>
        <input type="text" id="name" [(ngModel)]="userToUpdate.name" name="name" required>
      </div>
      <div>
        <label for="lastName">Apellido:</label>
        <input type="text" id="lastName" [(ngModel)]="userToUpdate.lastName" name="lastName" required>
      </div>
      <div>
        <label for="username">Nombre de Usuario:</label>
        <input type="text" id="username" [(ngModel)]="userToUpdate.username" name="username" required>
      </div>
      <div>
        <label for="password">Contraseña:</label>
        <input type="password" id="password" [(ngModel)]="userToUpdate.password" name="password" required>
      </div>
      <button type="submit">Actualizar Usuario</button>
    </form>
  </div>
